con:configuration.scriptText:
def response = new XmlSlurper().parseText(context.response)
def wrongWord = response.'**'.find { it.name() == 'word' }
def possibleVariants = response.'**'.findAll { it.name() == 's' }
def errorAttributes = response.'**'.find { it.name() == 'error' }.attributes()

log.info 'Error  ' + errorAttributes.get('code') + '. In "' + wrongWord + '" - possible variants: ' + possibleVariants

